mapper.select:
select bno,subject,b.teacher,title,regdate,content,viewcnt,username,writer
from kdemyreview b, kdemymember m
where b.writer=m.userid and bno=#{bno}

mapper.update:
update kdemyreview
set subject=#{subject}, teacher=#{teacher}, title=#{title}, content=#{content}
where bno=#{bno}
